package org.sunrise.dangdang.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.*;import org.sunrise.dangdang.bean.User;import org.sunrise.dangdang.service.UserService;import javax.annotation.Resource;@Controller@RequestMapping(value = "/user")public class UserLoginController {    @Resource    private UserService userService;    @RequestMapping(value = "/login",method = RequestMethod.GET,produces = {"application/json;charset=utf-8"})    @ResponseBody    public boolean login(@RequestParam(value = "email") String email, @RequestParam(value = "pwd") String pwd, Model model){        System.out.println("*-------"+email+pwd);        User user = userService.selectByPrimaryNameOrEamilAndPwd(email,pwd);        if (user != null){            model.addAttribute("user",user);            return true;        }        return false;    }}