package org.sunrise.dangdang.util;import java.text.SimpleDateFormat;import java.util.Date;public class DateFormat {    public static String formatDate(Date date,String template){        SimpleDateFormat simpleDateFormat = new SimpleDateFormat(template);        return simpleDateFormat.format(date);    }    public static String getCurrentTime(){        return getCurrentTime("yyyy-MM-dd-HH-mm-ss");    }    public static String getCurrentTime(String template){        Date date = new Date();        return formatDate(date,template);    }}