package org.sunrise.dangdang.util;import org.apache.commons.codec.binary.Base64;import java.security.MessageDigest;/** * 加密算法 */public class Encryption {    public static byte[] sha1ToBytes(String str) {        try {            MessageDigest digest = MessageDigest.getInstance("SHA-1");            byte[] bts = digest.digest(str.getBytes());            return bts;        } catch (Exception e) {            e.printStackTrace();        }        return null;    }    /**     * 加密后的Byte数组转换为String     * @param str     * @return     */    public static String byteBase64String(String str){        return  Base64.encodeBase64String(sha1ToBytes(str));    }}