package org.sunrise.dangdang.util;import org.sunrise.dangdang.bean.User;import javax.servlet.http.HttpServletRequest;public class EntityUtil {    public static User packagingUser(HttpServletRequest request,String email, String name, String password, String sex, String address){        User user = new User();        user.setId(UUIDUtil.createUUID());        user.setEmail(Encryption.byteBase64String(email));        user.setUsername(name);        user.setPassword(Encryption.byteBase64String(password));        user.setRegisterDate(DateFormat.getCurrentTime());        user.setLastDate(DateFormat.getCurrentTime());        user.setSex(sex);        user.setAddress(address);        user.setLastIp(RequestHost.getIpAddr(request));        return user;    }}